// Library code specifically for role api code
var Arrow = require('arrow'),
    Q = require('q'),
    Dtcarrow = require('dtcarrow');

// CRUD
module.exports.readAll = function() {
    var deferred = Q.defer();

    var Model = Arrow.getModel('appc.mysql.eolmig/Sport');
    Model.query({ }, function(err, results) {
        if (err) {
            deferred.reject({ success: false, status: 500, caller: '>getDbSport', data: err });
        }

        deferred.resolve({ success: true, status: 200, caller: '>getDbSport', data: results });
    });

    return deferred.promise;
};